// lore ipsum generator

import React from "react"

import Header from "./components/Header"

import Footer from "./components/Footer"

import "./styles/geral-style.css"


class App extends React.Component {      

    constructor() {
        super()
        this.state = {
            numeroParagrafos : 1,

            // paragrafo : "Mussum Ipsum, cacilds vidis litro abertis. Si num tem leite então bota uma pinga aí cumpadi! Quem num gosta di mé, boa gentis num é. Copo furadis é disculpa de bebadis, arcu quam euismod magna. Não sou faixa preta cumpadi, sou preto inteiris, inteiris.       ",

            paragrafos : [
              "11111Mussum Ipsum, cacilds vidis litro abertis. Si num tem leite então bota uma pinga aí cumpadi! Quem num gosta di mé, boa gentis num é. Copo furadis é disculpa de bebadis, arcu quam euismod magna. Não sou faixa preta cumpadi, sou preto inteiris, inteiris.        ",
              "22222Mussum Ipsum, cacilds vidis litro abertis. Si num tem leite então bota uma pinga aí cumpadi! Quem num gosta di mé, boa gentis num é. Copo furadis é disculpa de bebadis, arcu quam euismod magna. Não sou faixa preta cumpadi, sou preto inteiris, inteiris.        ",
              "33333Mussum Ipsum, cacilds vidis litro abertis. Si num tem leite então bota uma pinga aí cumpadi! Quem num gosta di mé, boa gentis num é. Copo furadis é disculpa de bebadis, arcu quam euismod magna. Não sou faixa preta cumpadi, sou preto inteiris, inteiris.        ",
              "44444Mussum Ipsum, cacilds vidis litro abertis. Si num tem leite então bota uma pinga aí cumpadi! Quem num gosta di mé, boa gentis num é. Copo furadis é disculpa de bebadis, arcu quam euismod magna. Não sou faixa preta cumpadi, sou preto inteiris, inteiris.        ",
              "55555Mussum Ipsum, cacilds vidis litro abertis. Si num tem leite então bota uma pinga aí cumpadi! Quem num gosta di mé, boa gentis num é. Copo furadis é disculpa de bebadis, arcu quam euismod magna. Não sou faixa preta cumpadi, sou preto inteiris, inteiris.        ",
              "66666Mussum Ipsum, cacilds vidis litro abertis. Si num tem leite então bota uma pinga aí cumpadi! Quem num gosta di mé, boa gentis num é. Copo furadis é disculpa de bebadis, arcu quam euismod magna. Não sou faixa preta cumpadi, sou preto inteiris, inteiris.        "
            ],


            ipsum : "000Mussum Ipsum, cacilds vidis litro abertis. Si num tem leite então bota uma pinga aí cumpadi! Quem num gosta di mé, boa gentis num é. Copo furadis é disculpa de bebadis, arcu quam euismod magna. Não sou faixa preta cumpadi, sou preto inteiris, inteiris.  "

        }

        this.addNumber = this.addNumber.bind(this)

        this.removeNumber = this.removeNumber.bind(this)

        this.gerarIpsum = this.gerarIpsum.bind(this)

        this.menor1 = this.menor1.bind(this)

        this.copyIpsum = this.copyIpsum.bind(this)

    }

    addNumber() {
        this.setState( 
            function(prevState){
                return(
                    {
                        numeroParagrafos : prevState.numeroParagrafos + 1
                    }

                )

        } )
    }

    menor1(){


        this.state.numeroParagrafos > 1 ? 

            this.removeNumber()
        :
            console.log("menor do que 1", this.state.numeroParagrafos)



        return(
            null

        )
    }



    removeNumber() {

        console.log("removeNumber rodando")

        this.setState(
            function(prevState) {

                return(

                    {
                        numeroParagrafos: prevState.numeroParagrafos - 1
                    }
                )
            }
        )

    }


    gerarIpsum() {
      this.setState(
          function(){

            function random(max) {
              const min = 0;
              max = Math.floor(max);
              return Math.floor(Math.random() * (max - min)) + min;
            }

            let randomNumbers = []

            for (var i = 0; i < this.state.numeroParagrafos; i++) {

              randomNumbers.push(random(this.state.paragrafos.length))
              console.log("rodando")

            }            

            let imprimirParagrafos = []

            for (var i = 0; i < this.state.numeroParagrafos; i++) {

              imprimirParagrafos.push(this.state.paragrafos[randomNumbers[i]])

            }

            return(
                {
                    ipsum: imprimirParagrafos
                }
            )
          }
      )
  }

    copyIpsum() {
      this.setState(
          function(){

            var textField = document.createElement('textarea')
            textField.innerText = this.state.ipsum
            document.body.appendChild(textField)
            textField.select()
            document.execCommand('copy')
            textField.remove()



              return(

                null

              )
          }
      )
  } 

    render() {
        return(

            <div>                
                <Header />
                <div className = "principal">

                    <div className="text">

                        <h1 className = "texto-paragrafo">
                            Preciso de { this.state.numeroParagrafos}  parágrafo(s).
                        </h1>
                        <div className = "botoes-add-remove">

                            <div>
                                <button className ="add" className = "button-add-remove" onClick = {this.addNumber}> 
                                +
                                </button>
                            </div>

                            <div>
                                <button className ="remove" className = "button-add-remove" onClick = {this.menor1}> 
                                    -
                                </button>

                            </div>

                        </div>                        

                        <button className = "button-generate" onClick = {this.gerarIpsum} >
                            Gerar 
                        </button>

                    </div>

                    <div className = "ipsum-containner">               

                        <h2 className ="ipsum-box">
                            {this.state.ipsum}
                        </h2>

                    </div>

                    <button className = "button-copy" onClick = {this.copyIpsum} >
                            Copiar 
                    </button>

                </div>

                <Footer />

            </div>
        )
    }
}


export default App
